import React from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useData } from '../../contexts/DataContext';

export const DashboardLayout: React.FC = () => {
  const { user, logout } = useAuth();
  const { activeTab, setActiveTab, addLog } = useData();

  const handleLogout = () => {
    addLog('User logged out', 'info');
    logout();
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 relative overflow-hidden">
      {/* Animated Background */}
      <div className="absolute inset-0">
        <div className="absolute top-0 left-0 w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute bottom-0 right-0 w-96 h-96 bg-cyan-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
      </div>

      <div className="relative z-10 p-6">
        <div className="max-w-7xl mx-auto">
          {/* Header */}
          <div className="flex justify-between items-center mb-8">
            <div className="text-center flex-1">
              <h1 className="text-6xl font-bold bg-gradient-to-r from-white via-cyan-200 to-blue-200 bg-clip-text text-transparent mb-4">
                üèõÔ∏è ‡§∏‡§∞‡§ï‡§æ‡§∞‡•Ä ‡§Ü‡§µ‡•á‡§¶‡§® ‡§™‡•ç‡§∞‡§¨‡§Ç‡§ß‡§® ‡§°‡•à‡§∂‡§¨‡•ã‡§∞‡•ç‡§° üèõÔ∏è
              </h1>
              <p className="text-2xl text-cyan-200 font-semibold">Government Application Management System</p>
            </div>

            {/* User Info */}
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <p className="text-white text-sm">{user?.email}</p>
                <p className="text-white/60 text-xs capitalize">{user?.role}</p>
              </div>
              <button
                onClick={handleLogout}
                className="px-4 py-2 bg-red-500/20 hover:bg-red-500/30 text-white rounded-lg transition-colors"
              >
                Logout
              </button>
            </div>
          </div>

          {/* Navigation */}
          <div className="flex justify-center gap-4 mb-8">
            {['dashboard', 'upload', 'issues'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`px-6 py-3 rounded-xl font-medium transition-all capitalize ${
                  activeTab === tab 
                    ? 'bg-white text-gray-800 shadow-lg' 
                    : 'bg-white/20 text-white hover:bg-white/30'
                }`}
              >
                {tab}
              </button>
            ))}
          </div>

          {/* Content */}
          <div className="bg-white/20 backdrop-blur-md rounded-2xl p-8 border border-white/20">
            <div className="text-center text-white">
              <h2 className="text-2xl font-bold mb-4">üöÄ Dashboard Ready!</h2>
              <p className="text-lg">Current Tab: <span className="capitalize font-bold">{activeTab}</span></p>
              <p className="mt-4 text-white/80">Modular structure successfully implemented!</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
